public class MyDemo {
    public static void main(String[] args) {
        MyTest mt = (a, b, c) -> a.substring(b, c);
        // 上面这一句相当于:
        // MyTest mt = String::subString;
        // 这和a.subString(b, c)效果一样
        String str = mt.test("hello world", 2, 3);
        System.out.println(str);
    }
}

@FunctionalInterface
interface MyTest {
