                    rowSet.updateFloat("score", 99.9f);
                    rowSet.updateRow(); // 同步修改到底层数据库
                }
            }
        }
    }

    public static void main(String[] args) throws Exception {
        JdbcRowSetTest manage = new JdbcRowSetTest();
        manage.update("select * from student");
    }
}

